import React, { Component } from "react";
import axios from "axios";
import Fullscreen from "../assets/icons/SVG/Icon-fullscreen.svg";
import Volume from "../assets/icons/SVG/Icon-volume.svg";

const videoUrl =
  "https://project-2-api.herokuapp.com/videos/1af0jruup5gu?api_key=46a5c4b9-e4ed-4f7e-bbf7-be32f1a02279";

class Hero extends Component {
  state = {
    mainVideo: []
  };
  componentDidMount() {
    axios.get(videoUrl).then(response => {
      const newVideo = response.data.map(video => {
        return {
          image: video.image
        };
      });

      this.setState({
        mainVideo: newVideo
      });
    });
  }
  render() {
    return (
      <section id="background">
        <div className="hero">
          <div className="hero__play">
            <video poster="./assets/images/video-list-0.jpg" />
          </div>
          <div className="hero__scroll">
            <img id="play" src={this.state.mainVideo} alt="play-button" />
            <div className="hero__scroll--hr">
              <hr /> <p>0:00 / 0:42</p>
            </div>
            <div className="hero__scroll--stuffs">
              <img id="fullscreen" src={Fullscreen} alt="curser" />
              <img id="volume" src={Volume} alt="volume" />
            </div>
          </div>
        </div>
      </section>
    );
  }
}

export default Hero;
