import React, { Component } from "react";
import Mohan from "../Assets/Images/Mohan-muruge.jpg";

export default class NewComment extends Component {
  render() {
    const submitHandler = event => {
      event.preventDefault();
      this.setState({
        name: ""
      });
      // 2. Get the item and pass the item to our parent
    };

    return (
      <div className="newComment">
        <h4 className="newComment__num">3 Comments</h4>
        <div className="newComment__new">
          <img id="Mohan" src={Mohan} />
          <div className="newComment__new--comment">
            <div id="setAside">
              <label>JOIN THE CONVERSATION</label>
              <textarea
                placeholder=" Add a new comment"
                id="inputComment"
                type="text"
                name="text"
                rows="7"
                cols="50"
                value={this.state.name}
              />
            </div>
            <button id="myButton" type="submit" onClick={submitHandler()}>
              COMMENT
            </button>
          </div>
        </div>
      </div>
    );
  }
}
