import React from "react";
import "./styles/main.css";

function CommentList(props) {
  const comments = props.comments.map(function(user) {
    return <Comment Name={user.Name} Date={user.Date} Comment={user.Comment} />;
  });

  return <div>{comments}</div>;
}

function Comment(props) {
  return (
    <div>
      <img className="users__img" src=".\Assets\Images\e1e1e1.png" />
      <div className="users__comment">
        {props.Name} {props.Date} {props.Comment}
      </div>
    </div>
  );
}

export default CommentList;
