import React, { Component } from "react";
import Views from "../assets/icons/SVG/Icon-views.svg";
import Likes from "../assets/icons/SVG/Icon-likes.svg";

const initialVideo = this.props.videos[0];

class Clip extends Component {
  state = {
    video: initialVideo
  };

  render() {
    return (
      <section className="clip">
        <div className="clip__title">{this.state.title}</div>
        <div id="clip">
          <div className="clip__letters">
            <div id="channel">{this.state.channel}</div>
            <div id="date">{this.state.timestamp}</div>
          </div>
          <div className="clip__likes">
            <div id="views">
              <img src={Views} alt="views" />
              {this.state.views}
            </div>
            <div id="hearts">
              <img src={Likes} alt="likes" />
              {this.state.likes}
            </div>
          </div>
        </div>
        <div className="clip__description">{this.props.description}</div>
      </section>
    );
  }
}

export default Clip;
