import React from "react";
import "./App.css";
//import Mohan from "../public/Assets/Images/Mohan-muruge.jpg";

//block__element--modifier--state

function App() {
  const users = [
    {
      Avatar: "/assets/images/e1e1e1.png",
      Name: "Micheal Lyons",
      Date: "12/18/2018",
      Comment: `They BLEW the ROOF off at their last show, 
      once everyone started figuring out they were going.
      This is still simply the greatest opening of a concert 
      I have EVER witnessed.`
    },

    {
      Avatar: "/assets/images/e1e1e1.png",
      Name: "Gary Wong",
      Date: "12/18/2018",
      Comment: `Every time I see him shred I feel so 
      motivated to get off my couch and hop on my board.
      He’s so talented! I wish I can ride like him one day 
      so I can really enjoy myself!`
    },

    {
      Avatar: "/assets/images/e1e1e1.png",
      Name: "Theodore Duncan",
      Date: "11/15/2018",
      Comment: `How can someone be so good!!! 
      You can tell he lives for this and loves 
      to do it every day. Everytime I see him I feel 
      instantly happy! He’s definitely my favorite ever!`
    }
  ];

  return (
    <div className="App">
      <Header />
      <CommentList comments={users} />
    </div>
  );
}

function Header() {
  return (
    <header className="header">
      <img className="header__logo" src="assets/logo/Logo-brainflix.svg" />
      <input className="search" placeholder="Search..." />
      <div className="header__right">
        <button className="upload" type="button" name="upload">
          + UPLOAD
        </button>
        <img className="Mohan" src="assets/images/Mohan-muruge.jpg" />
      </div>
    </header>
  );
}

function CommentList(props) {
  const comments = props.comments.map(function(user) {
    return (
      <Comment
        Avatar={user.Avatar}
        Name={user.Name}
        Date={user.Date}
        Comment={user.Comment}
      />
    );
  });

  return <div>{comments}</div>;
}

function Comment(props) {
  return (
    <div>
      <img className="users--img" src={props.Avatar} />
      <div className="users">
        {props.Name} {props.Date} {props.Comment}
      </div>
    </div>
  );
}

export default App;
