import React, { Component } from "react";
import axios from "axios";
import "../styles/main.css";
import Header from "./Header";
import Hero from "./Hero";
import Clip from "./Clip";
import NewComment from "./NewComment";
import CommentList from "../container/CommentList";
import VideoList from "../container/VideoList";

const videoUrl =
  "https://project-2-api.herokuapp.com/videos?api_key=46a5c4b9-e4ed-4f7e-bbf7-be32f1a02279";

class App extends Component {
  state = {
    videoContents: []
  };

  componentDidMount() {
    axios.get(videoUrl).then(response => {
      const newVideos = response.data.map(video => {
        return {
          id: video.id,
          title: video.title,
          channel: video.channel,
          image: video.image,
          description: video.description,
          views: video.views,
          likes: video.likes,
          duration: video.duration,
          video: video.video,
          timestamp: video.timestamp
        };
      });

      this.setState({
        videoContents: newVideos
      });
    });
  }

  render() {
    return (
      <div className="App">
        <Header history={this.props.history} />
        <Hero />
        <div className="box">
          <div className="boxClip">
            <Clip />
            <NewComment />
            <CommentList />
          </div>
          <VideoList />
        </div>
      </div>
    );
  }
}

export default App;
